(()=>{const e=document.body,t=document.querySelector("#cursor"),s=document.querySelectorAll("a");e.addEventListener("mousemove",(e=>{t.style.transform=`translate(${e.clientX-8}px, ${e.clientY-8}px)`})),e.addEventListener("mouseenter",(e=>{t.style.display="block"})),e.addEventListener("mouseleave",(e=>{t.style.display="none"})),s.forEach((e=>{e.addEventListener("mouseover",(()=>{t.style.width="10px",t.style.height="10px"})),e.addEventListener("mouseout",(()=>{t.style.width="16px",t.style.height="16px"}))}))})();