(()=>{const e=document.body,t=document.querySelector("#cursor"),s=document.querySelectorAll("a");e.addEventListener("mousemove",(e=>{t.style.transform=`translate(${e.clientX-8}px, ${e.clientY-8}px)`})),e.addEventListener("mouseenter",(e=>{t.style.display="block"})),e.addEventListener("mouseleave",(e=>{t.style.display="none"})),s.forEach((e=>{e.addEventListener("mouseover",(()=>{t.style.width="10px",t.style.height="10px"})),e.addEventListener("mouseout",(()=>{t.style.width="16px",t.style.height="16px"}))}));const o=document.querySelector("#profile-image-link"),r=document.querySelector("#profile-photo");o.addEventListener("mouseover",(()=>{r.classList.remove("opacity-0"),r.classList.add("opacity-100"),r.style.transform="translate(0px, -10px)"})),o.addEventListener("mouseout",(()=>{r.classList.remove("opacity-100"),r.classList.add("opacity-0"),r.style.transform="translate(0px, 10px)"}))})();